(function(){$(function(){let n=0;const a=Date.now();$.ajax("https://home.zdul.xyz/api/time.php").done(function(t){const e=Date.now()-a;n=Date.now()-e/2-parseInt(t);console.log(n)});$(".match-submit-button").click(function(){const n=t($(this).parents("form").serializeArray());$.ajax("/prediction",{method:"POST",dataType:"json",contentType:"json",data:JSON.stringify(n)}).done(function(t){e(t["message"])})});$(".pick-lock-time").each(function(){$(this).data("interval-id",setInterval(s.bind(this),1e3))});function e(t,n=2500){const a=$("#snackbar");a.text(t);a.addClass("show");setTimeout(()=>a.removeClass("show"),n)}function s(){const t=parseInt($(this).data("match-start"));const a=Math.round((t*1e3-Date.now()-n)/1e3);let e="";const s=Math.floor(a/86400);if(s>0){e+=`${s} day${s===1?" ":"s "}`}if(a<0){$(this).parent().find("span,small").removeClass("font-weight-bold text-light").addClass("text-muted");$(this).parent().removeClass("bg-danger");$(this).text("Locked");clearInterval(parseInt($(this).data("interval-id")));return}if(a<60*60){$(this).parent().find("span,small").addClass("font-weight-bold text-light").removeClass("text-muted");$(this).parent().addClass("bg-danger")}const o=String(Math.floor(a/3600)%24).padStart(2,"0");const i=String(Math.floor(a/60)%60).padStart(2,"0");const r=String(a%60).padStart(2,"0");e+=`${o}:${i}:${r}`;$(this).text("Picks lock in "+e)}});function t(t){const n={};for(let a=0;a<t.length;a++){n[t[a].name]=t[a].value}return n}})();