(function(){$(function(){$(".match-submit-button").click(function(){const n=t($(this).parents("form").serializeArray());$.ajax("/prediction",{method:"POST",dataType:"json",contentType:"json",data:JSON.stringify(n)}).done(function(t){a(t["message"])})});$(".pick-lock-time").each(function(){$(this).data("interval-id",setInterval(n.bind(this),1e3))});function a(t,a=2500){const n=$("#snackbar");n.text(t);n.addClass("show");setTimeout(()=>n.removeClass("show"),a)}function n(){const t=parseInt($(this).data("match-start"));const a=Math.round((t*1e3-Date.now())/1e3);let n="";const e=Math.floor(a/86400);if(e>0){n+=`${e} day${e===1?" ":"s "}`}if(a<0){$(this).parent().find("span,small").removeClass("font-weight-bold text-light").addClass("text-muted");$(this).parent().removeClass("bg-danger");$(this).text("Locked");clearInterval(parseInt($(this).data("interval-id")));return}if(a<60*60){$(this).parent().find("span,small").addClass("font-weight-bold text-light").removeClass("text-muted");$(this).parent().addClass("bg-danger")}const s=String(Math.floor(a/3600)%24).padStart(2,"0");const i=String(Math.floor(a/60)%60).padStart(2,"0");const o=String(a%60).padStart(2,"0");n+=`${s}:${i}:${o}`;$(this).text("Picks lock in "+n)}});function t(t){const a={};for(let n=0;n<t.length;n++){a[t[n].name]=t[n].value}return a}})();