(function(){$(function(){let a=0;const n=Date.now();$.ajax("https://home.zdul.xyz/api/time.php").done(t=>{const e=Date.now()-n;a=Date.now()-e/2-parseInt(t)});$(".match-submit-button").click(function(){const a=t($(this).parents("form").serializeArray());$.ajax("/prediction",{method:"POST",dataType:"json",contentType:"json",data:JSON.stringify(a)}).done(t=>{e(t["message"])})});$(".pick-lock-time").each(function(){$(this).data("interval-id",setInterval(s.bind(this),1e3))});function e(t,a=2500){const n=$("#snackbar");n.text(t);n.addClass("show");setTimeout(()=>n.removeClass("show"),a)}function s(){const t=parseInt($(this).data("match-start"));const n=Math.round((t*1e3-Date.now()-a)/1e3);let e="";const s=Math.floor(n/86400);if(s>0){e+=`${s} day${s===1?" ":"s "}`}if(n<0){$(this).parent().find("span,small").removeClass("font-weight-bold text-light").addClass("text-muted");$(this).parent().removeClass("bg-danger");$(this).text("Locked");clearInterval(parseInt($(this).data("interval-id")));return}if(n<60*60){$(this).parent().find("span,small").addClass("font-weight-bold text-light").removeClass("text-muted");$(this).parent().addClass("bg-danger")}const o=String(Math.floor(n/3600)%24).padStart(2,"0");const i=String(Math.floor(n/60)%60).padStart(2,"0");const r=String(n%60).padStart(2,"0");e+=`${o}:${i}:${r}`;$(this).text("Picks lock in "+e)}});function t(t){const a={};for(let n=0;n<t.length;n++){a[t[n].name]=t[n].value}return a}})();