(function(){$(function(){$(".match-submit-button").click(function(){const o=t($(this).parents("form").serializeArray());$.ajax("/prediction",{method:"POST",dataType:"json",contentType:"json",data:JSON.stringify(o)}).done(function(t){n(t["message"])})});$(".pick-lock-time").each(function(){setInterval(o.bind(this),1e3)});function n(t,n=2500){const o=$("#snackbar");o.text(t);o.addClass("show");setTimeout(()=>o.removeClass("show"),n)}function o(t){const n=parseInt($(this).data("match-start"));const o=Math.round((n*1e3-Date.now())/1e3);let a="";const s=Math.floor(o/86400);if(s>0){a+=`${s} day${s===1?" ":"s "}`}if(o<60*60){$(this).addClass("font-weight-bold")}const e=String(Math.floor(o/3600)%24).padStart(2,"0");const i=String(Math.floor(o/60)%60).padStart(2,"0");const c=String(o%60).padStart(2,"0");a+=`${e}:${i}:${c}`;$(this).text("Picks lock in "+a)}});function t(t){const n={};for(let o=0;o<t.length;o++){n[t[o].name]=t[o].value}return n}})();